    public double getTX()
    {
        return Double.parseDouble(tx.getText());
    }
    
    public double getTY()
    {
        return Double.parseDouble(ty.getText());
    }
    
    public double getThetaDegrees()
    {
        return Double.parseDouble(theta.getText());
    }
    
    public double getThetaRadians()
    {
        return getThetaDegrees()/Math.PI*180;
    }
